mapper.select:
SELECT COUNT(*)
FROM CC_RE
WHERE CC_ID = #{cc_id}

SELECT NVL(SUM(CC_ING_PRICE), 0) AS INGPRICE
FROM CC_ING
WHERE CC_ID = #{cc_id}

SELECT NVL(SUM(CC_ETC_PRICE), 0) AS ETCPRICE
FROM CC_ETC
WHERE CC_ID = #{cc_id}

SELECT ROWNUM AS CC_TIME_NUM, CC_ID, TO_CHAR(CC_TIME_DATE, 'YYYY-MM-DD') AS CC_TIME_DATE , CC_TIME_START, CC_TIME_NAME
FROM CC_TIME
WHERE CC_ID = #{cc_id}

SELECT COUNT(*)
FROM CC_REPLY
WHERE CC_CO_ID = 1;
